import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { FaArrowUp, FaArrowDown } from "react-icons/fa";

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get("http://localhost:3002/allOrders") // Replace with your backend URL
      .then((response) => {
        setOrders(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching orders:", error);
        setLoading(false);
      });
  }, []);

  return (
    <div className="flex flex-col items-center p-6 bg-gray-100 min-h-screen">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">My Orders</h2>

      {loading ? (
        <p className="text-gray-600 text-lg">Loading orders...</p>
      ) : orders.length === 0 ? (
        <div className="bg-white p-6 shadow-lg rounded-lg text-center">
          <p className="text-gray-600 text-lg">You haven't placed any orders today.</p>
          <Link
            to="/"
            className="mt-4 px-5 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition"
          >
            Place New Order
          </Link>
        </div>
      ) : (
        <div className="w-full max-w-5xl bg-white shadow-lg rounded-lg overflow-hidden">
          <div className="overflow-x-auto">
            <table className="w-full border-collapse text-gray-800">
              <thead>
                <tr className="bg-blue-600 text-white text-lg">
                  <th className="py-4 px-6 text-left">Order ID</th>
                  <th className="py-4 px-6 text-left">Stock</th>
                  <th className="py-4 px-6 text-center">Quantity</th>
                  <th className="py-4 px-6 text-right">Price</th>
                  <th className="py-4 px-6 text-center">Mode</th>
                </tr>
              </thead>
              <tbody>
                {orders.map((order) => (
                  <tr key={order._id} className="border-b hover:bg-gray-100 transition">
                    <td className="py-4 px-6">{order._id.slice(-6).toUpperCase()}</td>
                    <td className="py-4 px-6 font-semibold">{order.name}</td>
                    <td className="py-4 px-6 text-center">{order.qty}</td>
                    <td className="py-4 px-6 text-right font-bold">₹{order.price}</td>
                    <td className="py-4 px-6 text-center">
                      <span
                        className={`inline-flex items-center px-3 py-1 rounded-full text-white font-medium ${
                          order.mode === "BUY" ? "bg-green-500" : "bg-red-500"
                        }`}
                      >
                        {order.mode} {order.mode === "BUY" ? <FaArrowUp className="ml-2" /> : <FaArrowDown className="ml-2" />}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default Orders;
