import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { FaArrowUp, FaArrowDown } from "react-icons/fa"; // Import icons

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get("http://localhost:3002/allOrders")
      .then((response) => {
        setOrders(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching orders:", error);
        setLoading(false);
      });
  }, []);

  return (
    <div className="flex flex-col items-center p-6 bg-gray-100 min-h-screen">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">ðŸ“¦ My Orders</h2>

      {loading ? (
        <p className="text-gray-600 text-lg">Loading orders...</p>
      ) : orders.length === 0 ? (
        <div className="bg-white p-6 shadow-lg rounded-lg text-center">
          <p className="text-gray-600 text-lg">You haven't placed any orders today</p>
          <Link
            to="/"
            className="mt-4 px-5 py-3 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600 transition"
          >
            Place New Order
          </Link>
        </div>
      ) : (
        <div className="w-full max-w-4xl bg-white shadow-lg rounded-lg overflow-hidden">
          <table className="w-full border-collapse">
            <thead>
              <tr className="bg-blue-600 text-white text-lg">
                <th className="py-4 px-5 text-left">Order ID</th>
                <th className="py-4 px-5 text-left">Stock</th>
                <th className="py-4 px-5 text-left">Quantity</th>
                <th className="py-4 px-5 text-left">Price</th>
                <th className="py-4 px-5 text-left">Mode</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order, index) => (
                <tr
                  key={order._id}
                  className={`border-b ${
                    index % 2 === 0 ? "bg-gray-50" : "bg-white"
                  } hover:bg-gray-100 transition`}
                >
                  <td className="py-4 px-5 font-mono text-gray-700">{order._id}</td>
                  <td className="py-4 px-5 font-semibold text-gray-800">{order.name}</td>
                  <td className="py-4 px-5 text-gray-700">{order.qty}</td>
                  <td className="py-4 px-5 text-gray-900 font-bold">â‚¹{order.price}</td>
                  <td className="py-4 px-5">
                    <span
                      className={`flex items-center justify-center px-3 py-1 rounded-full text-white text-sm font-semibold ${
                        order.mode === "BUY" ? "bg-green-500" : "bg-red-500"
                      }`}
                    >
                      {order.mode === "BUY" ? <FaArrowUp className="mr-1" /> : <FaArrowDown className="mr-1" />}
                      {order.mode}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Orders;
